mapper.select:

select id, name, email, gender
  from user
 where id = #{id}


select id, name, email, gender
  from user
 where email = #{email}

choose.when:

update user
   set name=#{name},
       gender=#{gender}
 where id=#{id}

choose.otherwise:

update user
   set name=#{name},
       password=password(#{password}),
       gender=#{gender}
 where id=#{id}
